import React, { useState, useEffect } from 'react';

function App() {
  const [users, setUsers] = useState([]);
  const [name, setName] = useState('');
  const [userIdToDelete, setUserIdToDelete] = useState('');
  const apiUrl = 'http://localhost:3000';

  // Fetch users data
  const fetchUsers = async () => {
    try {
      const response = await fetch(`${apiUrl}/`);
      const data = await response.json();
      setUsers(data);
    } catch (error) {
      console.error('Error fetching users:', error);
    }
  };

  useEffect(() => {
    fetchUsers();
  }, []); // Runs only once when the component mounts

  // Handle add user
  const handleAddUser = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch(`${apiUrl}/`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ name }),
      });

      if (response.ok) {
        const result = await response.text();
        alert(result); // Show success message
        setName('');
        fetchUsers(); // Refresh the list of users
      } else {
        alert('Failed to add user');
      }
    } catch (error) {
      console.error('Error adding user:', error);
    }
  };

  // Handle delete user
  const handleDeleteUser = async () => {
    if (!userIdToDelete) {
      alert('Please provide a user ID to delete.');
      return;
    }
    try {
      const response = await fetch(`${apiUrl}/?id=${userIdToDelete}`, {
        method: 'DELETE',
      });

      if (response.ok) {
        alert(`User with ID ${userIdToDelete} deleted.`);
        setUserIdToDelete('');
        fetchUsers(); // Refresh the list of users
      } else {
        alert('Failed to delete user');
      }
    } catch (error) {
      console.error('Error deleting user:', error);
    }
  };

  return (
    <div style={{ margin: '0 auto', padding: '20px', width: '80%', maxWidth: '600px' }}>
      <h1 style={{ textAlign: 'center', color: '#007bff' }}>User Management</h1>

      <div style={{ display: 'flex', justifyContent: 'space-between', marginBottom: '20px' }}>
        <div style={{ flex: '1', marginRight: '10px' }}>
          <h2>Add a User</h2>
          <form onSubmit={handleAddUser}>
            <input
              type="text"
              value={name}
              onChange={(e) => setName(e.target.value)}
              placeholder="Enter user name"
              required
              style={{ padding: '10px', width: '100%', marginBottom: '10px', borderRadius: '4px', border: '1px solid #ccc' }}
            />
            <button type="submit" style={{ padding: '10px 20px', backgroundColor: '#007bff', color: '#fff', border: 'none', borderRadius: '4px' }}>
              Add User
            </button>
          </form>
        </div>

        <div style={{ flex: '1', marginLeft: '10px' }}>
          <h2>Delete a User</h2>
          <input
            type="number"
            value={userIdToDelete}
            onChange={(e) => setUserIdToDelete(e.target.value)}
            placeholder="Enter user ID to delete"
            style={{ padding: '10px', width: '100%', marginBottom: '10px', borderRadius: '4px', border: '1px solid #ccc' }}
          />
          <button onClick={handleDeleteUser} style={{ padding: '10px 20px', backgroundColor: '#ff4d4d', color: '#fff', border: 'none', borderRadius: '4px' }}>
            Delete User
          </button>
        </div>
      </div>

      <div style={{ marginTop: '20px' }}>
        <h2>Users List</h2>
        <ul style={{ listStyleType: 'none', paddingLeft: '0' }}>
          {users.map((user) => (
            <li key={user.id} style={{ padding: '10px', margin: '10px 0', backgroundColor: '#f0f8ff', borderRadius: '4px' }}>
              {user.name} (ID: {user.id})
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
}

export default App;
