import React, { useState, useEffect } from 'react';

function App() {
  const [names, setNames] = useState([]);
  const [newName, setNewName] = useState('');
  const [message, setMessage] = useState('');

  useEffect(() => {
    fetchNames();
  }, []);

  const fetchNames = async () => {
    try {
      const response = await fetch('http://localhost.sportz.io:3000/names');
      const data = await response.json();
      setNames(data);
    } catch (error) {
      setMessage('Error fetching names');
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch('http://localhost.sportz.io:3000/names', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ name: newName }),
      });

      if (response.ok) {
        setNewName('');
        fetchNames();
        setMessage('Name added successfully!');
      } else {
        setMessage('Error adding name');
      }
    } catch (error) {
      setMessage('Error adding name');
    }
  };

  return (
    <div className="container mx-auto p-4">
      <h1 className="text-2xl font-bold mb-4">Name Management System</h1>

      <form onSubmit={handleSubmit} className="mb-6">
        <div className="flex gap-2">
          <input
            type="text"
            value={newName}
            onChange={(e) => setNewName(e.target.value)}
            placeholder="Enter a name"
            className="flex-1 p-2 border rounded"
            required
          />
          <button 
            type="submit"
            className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600"
          >
            Add Name
          </button>
        </div>
      </form>

      {message && (
        <div className="mb-4 p-2 bg-gray-100 rounded">
          {message}
        </div>
      )}

      <div className="bg-white rounded shadow">
        <h2 className="text-xl font-semibold p-4 border-b">Names List</h2>
        <ul className="divide-y">
          {names.map((item) => (
            <li>
              {item.name}
            </li>

          ))}
        </ul>
        {names.length === 0 && (
          <p className="p-4 text-gray-500">No names added yet.</p>
        )}
      </div>
    </div>
  );
}

export default App;
